import React, { useState } from 'react';
import axios from 'axios';

const SettingsPanel = ({ title, hand, pitch }) => {
  // TODO: Implement settings customization UI
  const [goal, setgoal] = useState('');
  const [min, setmin] = useState('');
  const [max, setmax] = useState('');
  const [target, settarget] = useState('');

  const handleOptionChange = (event) => {
    setgoal(event.target.value);
  };

  const handleMinChange = (event) => {
    setmin(event.target.value);
  };

  const handleMaxChange = (event) => {
    setmax(event.target.value);
  };
  const handleGoalChange = (event) => {
    settarget(event.target.value);
  };

  const handleSubmitBubbles = (event) => {
    event.preventDefault();
    console.log('Form submitted with selected option:', goal);

    settarget(1);
  };

  const handleSubmit = (event) => {

    let pitchType = pitch.replace(/\s+/g, '_');
    let pitch_type = `${hand}_${pitchType}`;

    const params = {
      pitch_type: pitch_type, 
        "min": { min}, 
        "max": {max},
        "target": {target},
        "goal": {goal}

    };


    axios.post('http://wmill33.pythonanywhere.com/api/new_metrics', params);

    console.log(params);

  };

  const boxStyle = {
    border: '10px solid #9f0a0a',
    padding: '5px',
    borderRadius: '5px',
    marginBottom: '10px',
    display: 'inline-block', 
    width: 'fit-content',
  };

  return (
    <div style={boxStyle}>
      <h2>{title}</h2>
      <form onSubmit={handleSubmitBubbles}>
      <div>
        <label>
          <input
            type="radio"
            value="Target"
            checked={goal === 'Target'}
            onChange={handleOptionChange}
          />
          Target
        </label>
        <label>
          <input
            type="radio"
            value="Edge"
            checked={goal === 'Edge'}
            onChange={handleOptionChange}
          />
          Edge
        </label>
      </div>
      <div>

        <input
          type="text"
          placeholder="Min"
          value={min}
          onChange={handleMinChange}
        />


      </div>
      <div>
        <input
          type="text"
          placeholder="Max"
          value={max}
          onChange={handleMaxChange}
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Goal"
          value={target}
          onChange={handleGoalChange}
        />
      </div>
      <button className='button'type="submit" onClick={handleSubmit}>Submit</button></form>
    </div>
  );
};

export default SettingsPanel;
