import { Title } from 'chart.js';
import React, { useState } from 'react';

const SettingsPanel = ({ title }) => {
  // TODO: Implement settings customization UI
  const [selectedOption, setSelectedOption] = useState('option1');
  const [input1Value, setInput1Value] = useState('');
  const [input2Value, setInput2Value] = useState('');
  const [input3Value, setInput3Value] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleInput1Change = (event) => {
    setInput1Value(event.target.value);
  };

  const handleInput2Change = (event) => {
    setInput2Value(event.target.value);
  };
  const handleInput3Change = (event) => {
    setInput3Value(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Form submitted with selected option:', selectedOption);
  };

  const boxStyle = {
    border: '10px solid #9f0a0a',
    padding: '5px',
    borderRadius: '5px',
    marginBottom: '10px',
    display: 'inline-block', 
    width: 'fit-content',
  };

  return (
    <div style={boxStyle}>
      <h2>{title}</h2>
      <form onSubmit={handleSubmit}>
      <div>
        <label>
          <input
            type="radio"
            value="Min"
            checked={selectedOption === 'Min'}
            onChange={handleOptionChange}
          />
          Target
        </label>
        <label>
          <input
            type="radio"
            value="Max"
            checked={selectedOption === 'Max'}
            onChange={handleOptionChange}
          />
          Edge
        </label>
      </div>
      <div>

        <input
          type="text"
          placeholder="Min"
          value={input1Value}
          onChange={handleInput1Change}
        />


      </div>
      <div>
        <input
          type="text"
          placeholder="Max"
          value={input2Value}
          onChange={handleInput2Change}
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Goal"
          value={input3Value}
          onChange={handleInput3Change}
        />
      </div>
      <button type="submit">Submit</button></form>
    </div>
  );
};

export default SettingsPanel;
