import React, { useEffect } from 'react';
import axios from 'axios';

const YourComponent = () => {
  // Sample pitch metrics (replace with actual data)
  const pitchMetrics = {
    velocity: 85,
    inducedVerticalBreak: 16,
    horizontalBreak: 9,
    spinRate: 2100,
    releaseHeight: 6.2,
    extension: 5.7,
    verticalApproachAngle: -5.3
  };

  const calculatePitchGrade = async () => {
    try {
      const response = await axios.post('http://localhost:5000/gradePitch', pitchMetrics);
      console.log('Pitch Grade:', response.data.pitchGrade);
    } catch (error) {
      console.error('Error calculating pitch grade:', error);
    }
  };

  useEffect(() => {
    // Call the function to calculate pitch grade when the component mounts
    calculatePitchGrade();
  }, []); // Empty dependency array to run once when the component mounts

  return (
    // Your component JSX
    'returnTest'
  );
};

export default YourComponent;
