import React, { useState } from 'react';

const PitchInputForm = () => {
  const [metrics, setMetrics] = useState({
    speed: '',
    accuracy: '',
    spinRate: ''
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setMetrics({
      ...metrics,
      [name]: value
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // TODO: Send metrics to the server for grading
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Speed:
        <input type="number" name="speed" value={metrics.speed} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Accuracy:
        <input type="number" name="accuracy" value={metrics.accuracy} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Spin Rate:
        <input type="number" name="spinRate" value={metrics.spinRate} onChange={handleInputChange} />
      </label>
      <br />
      <button type="submit">Grade Pitch</button>
    </form>
  );
};

export default PitchInputForm;
