import React, { useState } from 'react';

const PitchInputForm = () => {
  const [metrics, setMetrics] = useState({
    velocity: '',
    inducedVerticalBreak: '',
    horizontalBreak: '',
    spinRate: '',
    releaseHeight: '',
    extension: '',
    verticalApproachAngle: ''
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setMetrics({
      ...metrics,
      [name]: value
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // TODO: Handle form submission (e.g., send metrics to the server for grading)
    console.log('Form submitted with metrics:', metrics);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Velocity:
          <input type="number" name="velocity" value={metrics.velocity} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Induced Vertical Break:
          <input type="number" name="inducedVerticalBreak" value={metrics.inducedVerticalBreak} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Horizontal Break:
          <input type="number" name="horizontalBreak" value={metrics.horizontalBreak} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Spin Rate:
          <input type="number" name="spinRate" value={metrics.spinRate} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Release Height:
          <input type="number" name="releaseHeight" value={metrics.releaseHeight} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Extension:
          <input type="number" name="extension" value={metrics.extension} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Vertical Approach Angle:
          <input type="number" name="verticalApproachAngle" value={metrics.verticalApproachAngle} onChange={handleInputChange} />
        </label>
      </div>
      <button type="submit">Grade Pitch</button>
    </form>
  );
};

export default PitchInputForm;
