import React from 'react';
import { Line } from 'react-chartjs-2';
import { useLocation } from 'react-router-dom';

const GraphsPage = () => {
  const location = useLocation();
  const tableData = location.state?.tableData || [];

  const columns = [
    'Velocity', 
    'Ind. Vert Break', 'Horz Break', 'Spin Rate', 
    'RelHeight', 'Extension', 'Vert App Angle'
  ];

  const uniquePitchers = [...new Set(tableData.map(data => data[0]))];
  const uniquePitches = [...new Set(tableData.map(data => data[1]))];

  const getDatasetsForPitcherAndPitch = (pitcher, pitch) => {
    const filteredData = tableData.filter(row => row[0] === pitcher && row[1] === pitch);
    return columns.map((column, index) => {
      return {
        label: column,
        data: filteredData.map(row => row[index + 3]), // +3 to skip the first 3 columns ('Pitcher Name', 'Pitch Type', 'Hand')
        fill: false,
        borderColor: `rgb(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255})`
      };
    });
  };

  return (
    <div>
      {uniquePitchers.map(pitcher => (
        uniquePitches.map(pitch => (
          <div key={`${pitcher}-${pitch}`} style={{ margin: "20px 0" }}>
            <h3>{`${pitcher} - ${pitch}`}</h3>
            <Line
              data={{
                labels: columns,
                datasets: getDatasetsForPitcherAndPitch(pitcher, pitch)
              }}
            />
          </div>
        ))
      ))}
    </div>
  );
};

export default GraphsPage;
