import React, { useState } from 'react';
import './PitchInputForm.css';  // Import the CSS file for styling

const PitchInputForm = () => {
  const [metrics, setMetrics] = useState({
    pitchType: '',
    hand: '',
    velocity: '',
    inducedVerticalBreak: '',
    horizontalBreak: '',
    spinRate: '',
    releaseHeight: '',
    extension: '',
    verticalApproachAngle: ''
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setMetrics({
      ...metrics,
      [name]: value
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // TODO: Handle form submission (e.g., send metrics to the server for grading)
    console.log('Form submitted with metrics:', metrics);
  };

  return (
    <form onSubmit={handleSubmit} className="pitch-input-form">
      <div className="form-group">
        <label>
          Pitch Type:
          <input type="text" name="pitchType" value={metrics.pitchType} onChange={handleInputChange} className="form-control" />
        </label>
      </div>
      <div className="form-group">
        <label>
          Hand:
          <input type="text" name="hand" value={metrics.hand} onChange={handleInputChange} className="form-control" />
        </label>
      </div>
      {/* Rest of the input fields... */}
    </form>
  );
};

export default PitchInputForm;
