import React, { useState } from 'react';
import axios from 'axios';

const SettingsPanel = ({ title, hand, pitch }) => {
  // TODO: Implement settings customization UI
  const [selectedOption, setSelectedOption] = useState('');
  const [MinValue, setMinValue] = useState('');
  const [MaxValue, setMaxValue] = useState('');
  const [GoalValue, setGoalValue] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  const handleMinChange = (event) => {
    setMinValue(event.target.value);
  };

  const handleMaxChange = (event) => {
    setMaxValue(event.target.value);
  };
  const handleGoalChange = (event) => {
    setGoalValue(event.target.value);
  };

  const handleSubmitBubbles = (event) => {
    event.preventDefault();
    console.log('Form submitted with selected option:', selectedOption);

    setGoalValue(1);
  };

  const handleSubmit = (event) => {

    let pitchType = pitch.replace(/\s+/g, '_');
    let pitch_type = `${hand}_${pitchType}`;

    const params = {
      pitch_type: pitch_type, 
        "min": { MinValue}, 
        "max": {MaxValue},
        "target": {GoalValue},
        "goal": {selectedOption}

    };


    axios.post('http://wmill33.pythonanywhere.com/api/new_metrics', params);

    console.log(params);

  };

  const boxStyle = {
    border: '10px solid #9f0a0a',
    padding: '5px',
    borderRadius: '5px',
    marginBottom: '10px',
    display: 'inline-block', 
    width: 'fit-content',
  };

  return (
    <div style={boxStyle}>
      <h2>{title}</h2>
      <form onSubmit={handleSubmitBubbles}>
      <div>
        <label>
          <input
            type="radio"
            value="Target"
            checked={selectedOption === 'Target'}
            onChange={handleOptionChange}
          />
          Target
        </label>
        <label>
          <input
            type="radio"
            value="Edge"
            checked={selectedOption === 'Edge'}
            onChange={handleOptionChange}
          />
          Edge
        </label>
      </div>
      <div>

        <input
          type="text"
          placeholder="Min"
          value={MinValue}
          onChange={handleMinChange}
        />


      </div>
      <div>
        <input
          type="text"
          placeholder="Max"
          value={MaxValue}
          onChange={handleMaxChange}
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Goal"
          value={GoalValue}
          onChange={handleGoalChange}
        />
      </div>
      <button className='button'type="submit" onClick={handleSubmit}>Submit</button></form>
    </div>
  );
};

export default SettingsPanel;
